import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const initialProducts = [
  { id: 1, name: 'Nike Air Force 1 \'07 LV8', category: 'Shoes', stock: 220, price: 122.27 },
  { id: 2, name: 'Nike Sportswear Heritage86 Futura Washed', category: 'Caps', stock: 999, price: 15.95 },
  { id: 3, name: 'Nike Air Max Penny', category: 'Shoes', stock: 75, price: 182.50 },
  { id: 4, name: 'Nike Windrunner D.Y.E.', category: 'Jacket', stock: 30, price: 102.43 },
  { id: 5, name: 'Nike Storm-FIT x Stüssy', category: 'Jacket', stock: 50, price: 9.54 }
];

const ProductList = () => {
  const [products, setProducts] = useState(initialProducts);

  const handleDelete = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl mb-4">Product List</h1>
      <Link to="/add-product" className="bg-blue-500 text-white px-4 py-2 mb-4 inline-block">Add Product</Link>
      <input type="text" placeholder="Search products" className="border p-2 w-full mb-4" />
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">Name</th>
            <th className="py-2">Category</th>
            <th className="py-2">Stock</th>
            <th className="py-2">Price</th>
            <th className="py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td className="border px-4 py-2">{product.name}</td>
              <td className="border px-4 py-2">{product.category}</td>
              <td className="border px-4 py-2">{product.stock}</td>
              <td className="border px-4 py-2">${product.price.toFixed(2)}</td>
              <td className="border px-4 py-2">
                <Link to={`/edit-product/${product.id}`} className="bg-yellow-500 text-white px-2 py-1 rounded mr-2">Edit</Link>
                <button onClick={() => handleDelete(product.id)} className="bg-red-500 text-white px-2 py-1 rounded">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductList;
