import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const initialBrands = [
  { id: 1, name: 'Brand One', description: 'Description for Brand One', logo: 'brand-one-logo.png' },
  { id: 2, name: 'Brand Two', description: 'Description for Brand Two', logo: 'brand-two-logo.png' },
];

const BrandList = () => {
  const [brands, setBrands] = useState(initialBrands);

  const handleDelete = (id) => {
    setBrands(brands.filter(brand => brand.id !== id));
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl mb-4">Brand List</h1>
      <Link to="/add-brand" className="bg-blue-500 text-white px-4 py-2 mb-4 inline-block">Add Brand</Link>
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">Name</th>
            <th className="py-2">Description</th>
            <th className="py-2">Logo</th>
            <th className="py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {brands.map((brand) => (
            <tr key={brand.id}>
              <td className="border px-4 py-2">{brand.name}</td>
              <td className="border px-4 py-2">{brand.description}</td>
              <td className="border px-4 py-2"><img src={brand.logo} alt={brand.name} className="w-16 h-16" /></td>
              <td className="border px-4 py-2">
                <Link to={`/edit-brand/${brand.id}`} className="bg-yellow-500 text-white px-2 py-1 rounded mr-2">Edit</Link>
                <button onClick={() => handleDelete(brand.id)} className="bg-red-500 text-white px-2 py-1 rounded">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandList;
