import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './BrandList.css';

const initialBrands = [
  { id: 1, name: 'StrideStyle', description: 'StrideStyle merges the art of movement with the finesse of fashion. We offer a curated selection of footwear and apparel that not only speaks to your style but also supports your every step. Our designs are inspired by the latest trends, ensuring that every stride you take is both comfortable and chic. At StrideStyle, we believe in making every journey fashionable, whether it\'s a casual walk in the park or a bold stride on the runway.', logo: 'stridestylr-logo.png' },
  { id: 2, name: 'VogueVenture', description: 'VogueVenture is your gateway to the ultimate fashion adventure. We blend contemporary designs with timeless elegance to create a collection that is both versatile and trendsetting. From high-fashion shoes to stylish apparel, VogueVenture is committed to providing a dynamic and exciting shopping experience. Our brand is dedicated to those who seek to venture beyond the ordinary and make a statement with every outfit.', logo: 'vogueventure logo.png' },
];

const BrandList = () => {
  const [brands, setBrands] = useState(initialBrands);

  const handleDelete = (id) => {
    setBrands(brands.filter(brand => brand.id !== id));
  };

  return (
    <div className="brand-list-container">
      <h1 className="brand-list-title">Brand List</h1>
      <Link to="/add-brand" className="add-brand-button">Add Brand</Link>
      <table className="brand-table">
        <thead>
          <tr>
            <th className="table-header">Name</th>
            <th className="table-header">Description</th>
            <th className="table-header">Logo</th>
            <th className="table-header">Actions</th>
          </tr>
        </thead>
        <tbody>
          {brands.map((brand) => (
            <tr key={brand.id} className="brand-row">
              <td className="table-cell">{brand.name}</td>
              <td className="table-cell">{brand.description}</td>
              <td className="table-cell"><img src={brand.logo} alt={brand.name} className="brand-logo" /></td>
              <td className="table-cell">
                <Link to={`/edit-brand/${brand.id}`} className="edit-button">Edit</Link>
                <button onClick={() => handleDelete(brand.id)} className="delete-button">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandList;
